import React, { useState, useEffect } from 'react';

const SelectOption = () => {
  const [provincesData, setProvincesData] = useState(null);
  const [selectedProvince, setSelectedProvince] = useState(null);

  useEffect(() => {
    // Fetch the provinces data from the JSON file
    const fetchProvincesData = async () => {
      try {
        const response = await fetch('provinces.json');
        const data = await response.json();
        setProvincesData(data.provinces);
      } catch (error) {
        console.error('Error fetching provinces data:', error);
      }
    };

    fetchProvincesData();
  }, []);

  const handleProvinceSelect = (provinceId) => {
    const selected = provincesData && provincesData[provinceId];
    setSelectedProvince(selected);
  };

  return (
    <div>
      <h2>Province</h2>
      <select onChange={(e) => handleProvinceSelect(e.target.value)}>
        <option value="">Select a Province</option>
        {provincesData &&
          Object.keys(provincesData).map((provinceId) => (
            <option key={provinceId} value={provinceId}>
              {provincesData[provinceId].name.km}
            </option>
          ))}
      </select>

      {selectedProvince && (
        <div>
          <h2>Selected Province</h2>
          <p>
            <strong>Name:</strong> {selectedProvince.name.km} ({selectedProvince.name.latin})
          </p>
          <p>
            <strong>Administrative Unit:</strong>{' '}
            {selectedProvince.administrative_unit.km} ({selectedProvince.administrative_unit.latin})
          </p>
        </div>
      )}
    </div>
  );
};

export default SelectOption;
