import React, { useState } from 'react';
import provincesData from './ProvinceData';

const FetchData = () => {
  const { provinces } = provincesData;
  const [selectedProvince, setSelectedProvince] = useState(null);

  const handleProvinceSelect = (provinceId) => {
    const selected = provinces[provinceId];
    setSelectedProvince(selected);
  };

  return (
    <div>
      <p>
      <h2>Select a Province</h2>
      <select onChange={(e) => handleProvinceSelect(e.target.value)}>
        <option value="">Select a Province</option>
        {Object.keys(provinces).map((provinceId) => (
          <option key={provinceId} value={provinceId}>
            {provinces[provinceId].name.km}
          </option>
        ))}
      </select>
      </p>

      {/* <h2>Select a Communes</h2>
      <select>
        <option value="">Select Communes</option>
      </select> */}

      {/* <h2>Select a Districts</h2>
      <select>
        <option value="">Select Communes</option>
      </select>

      <h2>Select a Village</h2>
      <select>
        <option value="">Select Communes</option>
      </select> */}

      {selectedProvince && (
        <div>
          <h2>Selected Province</h2>
          <p>
            <strong>Name:</strong> {selectedProvince.name.km} ({selectedProvince.name.latin})
          </p>
          <p>
            <strong>Administrative Unit:</strong>{' '}
            {selectedProvince.administrative_unit.km} ({selectedProvince.administrative_unit.latin})
          </p>
        </div>
      )}
    </div>
  );
};

export default FetchData;
