import React,{ useState } from "react";
import provincesJson from './provinces.json'
import districtsjSON from './districts.json'

const SelectOption = () =>{
  const [selectedProvince, setSelectedProvince] = useState ('')
  const [selectedDistrict, setSelectedDistrict] = useState(null);

  const handlProvinceSelect = (provinceId) => {
    const selected = provincesJson.provinces[provinceId];
    setSelectedProvince(selected)
    setSelectedDistrict(null);
  }

  const handleDistrictSelect = (districtId) => {
    const selected = districtsjSON.districts[districtId];
    setSelectedDistrict(selected);
  };

  console.log(provincesJson);
  return (
    <div>
      <h2>Province</h2>
      <select onChange={(e) => handlProvinceSelect(e.target.value)}>
        <option value="">Select a province</option>
        {Object.keys(provincesJson.provinces).map((provinceId)=>(
            <option key = {provinceId} value = {provinceId}>{provincesJson.provinces[provinceId].name.latin} ({provincesJson.provinces[provinceId].name.km})</option>
        ))
        }
      </select>
      {selectedProvince && (
        <div>
          <h2>District</h2>
          <select onChange={(e) => handleDistrictSelect(e.target.value)}>
            <option value="">Select a District</option>
            {Object.keys(provincesJson.provinces[selectedProvince].districts).map((districtId) => (
              <option key={districtId} value={districtId}>
                {provincesJson.provinces[selectedProvince].districts[districtId].name.latin} ({provincesJson.provinces[selectedProvince].districts[districtId].name.km})
              </option>
            ))}
          </select>
        </div>
      )}

      {selectedDistrict && (
        <div>
          <h2>Selected District</h2>
          <p>
            <strong>Name:</strong> {selectedDistrict.name.km} ({selectedDistrict.name.latin})
          </p>
          <p>
            <strong>Administrative Unit:</strong> {selectedDistrict.administrative_unit.km} ({selectedDistrict.administrative_unit.latin})
          </p>
        </div>
      )}
    </div>
  )
};
export default SelectOption;




