// import React, { useState } from "react";
import provincesJson from './provinces.json'
import React, { useState, useEffect } from "react";

const SelectOption = () => {
  const [provincesData, setProvincesData] = useState([]);
  const [selectedProvince, setSelectedProvince] = useState('');

  useEffect(() => {
    // Fetching the JSON data
    fetch('./provinces.json')
      .then(response => response.json())
      .then(data => {
        setProvincesData(data.provinces);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleProvinceSelect = (e) => {
    setSelectedProvince(e.target.value);
  };

  return (
    <div>
      <h2>Province:</h2>
      <select onChange={handleProvinceSelect} value={selectedProvince}>
        <option value="">Select a province</option>
        {Object.keys(provincesData).map((provinceId) => (
          <option key={provinceId} value={provinceId}>
            {provincesData[provinceId].name.latin} ({provincesData[provinceId].name.km})
          </option>
        ))}
      </select>

      {selectedProvince && (
        <div>
          <h2>Your selected option is:</h2>
          <p>
            <strong>Province: </strong>
            {provincesData[selectedProvince].name.latin} ({provincesData[selectedProvince].name.km})
          </p>
        </div>
      )}
    </div>
  );
};

export default SelectOption;




