import React,{ useState } from "react";
import provincesJson from './provinces.json'
import districtsjSON from './districts.json'

const SelectOption = () =>{
  const [selectedProvince, setSelectedProvince] = useState ('')
  const [selectedDistrict, setSelectedDistrict] = useState(null);

  const handlProvinceSelect = (provinceId) => {
    const selected = provincesJson.provinces[provinceId];
    setSelectedProvince(selected)
    setSelectedDistrict(null);
  }

  const handleDistrictSelect = (districtId) => {
    const selected = selectedProvince.districts[districtId];
    setSelectedDistrict(selected);
  };

  console.log(provincesJson);
  return (
    <div>
      <h2>Province</h2>
      <select onChange={(e) => handlProvinceSelect(e.target.value)}>
        <option value="">Select a province</option>
        {Object.keys(provincesJson.provinces).map((provinceId)=>(
            <option key = {provinceId} value = {provinceId}>{provincesJson.provinces[provinceId].name.latin} ({provincesJson.provinces[provinceId].name.km})</option>
        ))
        }
      </select>
      <button onChange={selectedProvince}>Submit</button>
      {
        selectedProvince && (
          <div>
            <h2>Your selected option is :</h2>
            <p><strong>Province: </strong>{selectedProvince.name.latin} ({selectedProvince.name.km})</p>           
          </div>
        )
      }
    </div>
  )
}
export default SelectOption;




